// Variables del juego
let gameState = 'ready'; // ready, playing, paused, gameOver
let score = 0;
let lives = 3;
let level = 1;
let bricks = [];
let gameInterval;

// Elementos del juego
let ball = { x: 400, y: 400, dx: 3, dy: -3, radius: 6 };
let paddle = { x: 350, y: 430, width: 100, height: 12 };

// Inicializar juego
function initGame() {
  bricks = [];
  const brickData = [{"date":"2024-08-18","count":1,"level":1},{"date":"2024-08-19","count":0,"level":0},{"date":"2024-08-20","count":0,"level":0},{"date":"2024-08-21","count":7,"level":3},{"date":"2024-08-22","count":4,"level":2},{"date":"2024-08-23","count":0,"level":0},{"date":"2024-08-24","count":0,"level":0},{"date":"2024-08-25","count":0,"level":0},{"date":"2024-08-26","count":6,"level":3},{"date":"2024-08-27","count":7,"level":3},{"date":"2024-08-28","count":7,"level":3},{"date":"2024-08-29","count":7,"level":3},{"date":"2024-08-30","count":8,"level":4},{"date":"2024-08-31","count":2,"level":1},{"date":"2024-09-01","count":0,"level":0},{"date":"2024-09-02","count":8,"level":4},{"date":"2024-09-03","count":9,"level":4},{"date":"2024-09-04","count":0,"level":0},{"date":"2024-09-05","count":1,"level":1},{"date":"2024-09-06","count":1,"level":1},{"date":"2024-09-07","count":2,"level":1},{"date":"2024-09-08","count":2,"level":1},{"date":"2024-09-09","count":1,"level":1},{"date":"2024-09-10","count":0,"level":0},{"date":"2024-09-11","count":0,"level":0},{"date":"2024-09-12","count":0,"level":0},{"date":"2024-09-13","count":7,"level":3},{"date":"2024-09-14","count":2,"level":1},{"date":"2024-09-15","count":0,"level":0},{"date":"2024-09-16","count":0,"level":0},{"date":"2024-09-17","count":0,"level":0},{"date":"2024-09-18","count":0,"level":0},{"date":"2024-09-19","count":6,"level":3},{"date":"2024-09-20","count":7,"level":3},{"date":"2024-09-21","count":2,"level":1},{"date":"2024-09-22","count":1,"level":1},{"date":"2024-09-23","count":1,"level":1},{"date":"2024-09-24","count":9,"level":4},{"date":"2024-09-25","count":1,"level":1},{"date":"2024-09-26","count":0,"level":0},{"date":"2024-09-27","count":5,"level":3},{"date":"2024-09-28","count":2,"level":1},{"date":"2024-09-29","count":0,"level":0},{"date":"2024-09-30","count":0,"level":0},{"date":"2024-10-01","count":0,"level":0},{"date":"2024-10-02","count":0,"level":0},{"date":"2024-10-03","count":3,"level":2},{"date":"2024-10-04","count":0,"level":0},{"date":"2024-10-05","count":0,"level":0},{"date":"2024-10-06","count":0,"level":0},{"date":"2024-10-07","count":0,"level":0},{"date":"2024-10-08","count":0,"level":0},{"date":"2024-10-09","count":0,"level":0},{"date":"2024-10-10","count":6,"level":3},{"date":"2024-10-11","count":9,"level":4},{"date":"2024-10-12","count":2,"level":1},{"date":"2024-10-13","count":2,"level":1},{"date":"2024-10-14","count":0,"level":0},{"date":"2024-10-15","count":0,"level":0},{"date":"2024-10-16","count":0,"level":0},{"date":"2024-10-17","count":0,"level":0},{"date":"2024-10-18","count":0,"level":0},{"date":"2024-10-19","count":0,"level":0},{"date":"2024-10-20","count":1,"level":1},{"date":"2024-10-21","count":0,"level":0},{"date":"2024-10-22","count":2,"level":1},{"date":"2024-10-23","count":1,"level":1},{"date":"2024-10-24","count":0,"level":0},{"date":"2024-10-25","count":8,"level":4},{"date":"2024-10-26","count":2,"level":1},{"date":"2024-10-27","count":2,"level":1},{"date":"2024-10-28","count":4,"level":2},{"date":"2024-10-29","count":1,"level":1},{"date":"2024-10-30","count":8,"level":4},{"date":"2024-10-31","count":6,"level":3},{"date":"2024-11-01","count":2,"level":1},{"date":"2024-11-02","count":2,"level":1},{"date":"2024-11-03","count":2,"level":1},{"date":"2024-11-04","count":8,"level":4},{"date":"2024-11-05","count":5,"level":3},{"date":"2024-11-06","count":1,"level":1},{"date":"2024-11-07","count":0,"level":0},{"date":"2024-11-08","count":4,"level":2},{"date":"2024-11-09","count":1,"level":1}]; // Primeras 12 semanas

  for (let i = 0; i < Math.min(84, brickData.length); i++) {
    const row = Math.floor(i / 12);
    const col = i % 12;
    if (brickData[i].level > 0) {
      bricks.push({
        id: 'brick_' + i,
        x: 60 + col * 56,
        y: 80 + row * 22,
        level: brickData[i].level,
        destroyed: false,
        date: brickData[i].date,
        count: brickData[i].count
      });
    }
  }

  ball = { x: 400, y: 400, dx: 3, dy: -3, radius: 6 };
  paddle = { x: 350, y: 430, width: 100, height: 12 };
  updateDisplay();
}

// Iniciar juego
function startGame() {
  if (gameState === 'ready' || gameState === 'gameOver') {
    initGame();
    score = 0;
    lives = 3;
    level = 1;
  }

  gameState = 'playing';
  updateDisplay();

  if (gameInterval) clearInterval(gameInterval);
  gameInterval = setInterval(gameLoop, 50); // 20 FPS
}

// Pausar juego
function pauseGame() {
  if (gameState === 'playing') {
    gameState = 'paused';
    clearInterval(gameInterval);
  } else if (gameState === 'paused') {
    gameState = 'playing';
    gameInterval = setInterval(gameLoop, 50);
  }
  updateDisplay();
}

// Loop principal del juego
function gameLoop() {
  if (gameState !== 'playing') return;

  // Mover pelota
  ball.x += ball.dx;
  ball.y += ball.dy;

  // Colisiones con paredes
  if (ball.x <= ball.radius || ball.x >= 800 - ball.radius) {
    ball.dx = -ball.dx;
  }
  if (ball.y <= ball.radius) {
    ball.dy = -ball.dy;
  }

  // Colisión con paddle
  if (ball.y + ball.radius >= paddle.y && 
      ball.x >= paddle.x && 
      ball.x <= paddle.x + paddle.width &&
      ball.dy > 0) {
    ball.dy = -ball.dy;
    // Añadir efecto de ángulo basado en donde golpea
    const hitPos = (ball.x - paddle.x) / paddle.width;
    ball.dx = 6 * (hitPos - 0.5);
  }

  // Colisión con ladrillos
  bricks.forEach(function(brick) {
    if (!brick.destroyed &&
        ball.x >= brick.x && ball.x <= brick.x + 50 &&
        ball.y >= brick.y && ball.y <= brick.y + 18) {
      brick.destroyed = true;
      ball.dy = -ball.dy;
      score += brick.level * 10;

      // Ocultar ladrillo
      const brickElement = document.getElementById(brick.id);
      if (brickElement) {
        brickElement.classList.add('destroyed');
      }
    }
  });

  // Pelota sale por abajo
  if (ball.y > 500) {
    lives--;
    if (lives <= 0) {
      gameState = 'gameOver';
      clearInterval(gameInterval);
    } else {
      ball = { x: 400, y: 400, dx: 3, dy: -3, radius: 6 };
    }
  }

  // Verificar victoria
  const remainingBricks = bricks.filter(function(b) { return !b.destroyed; }).length;
  if (remainingBricks === 0) {
    level++;
    // Reiniciar nivel con más velocidad
    ball.dx *= 1.1;
    ball.dy *= 1.1;
    initGame();
  }

  updateDisplay();
}

// Actualizar elementos visuales
function updateDisplay() {
  // Actualizar posición de la pelota
  const ballElement = document.getElementById('game-ball');
  if (ballElement) {
    ballElement.setAttribute('cx', ball.x);
    ballElement.setAttribute('cy', ball.y);
  }

  // Actualizar posición del paddle
  const paddleElement = document.getElementById('game-paddle');
  if (paddleElement) {
    paddleElement.setAttribute('x', paddle.x);
  }

  // Actualizar puntajes
  const scoreElement = document.getElementById('score-display');
  if (scoreElement) {
    scoreElement.textContent = 'Puntos: ' + score;
  }

  const livesElement = document.getElementById('lives-display');
  if (livesElement) {
    livesElement.textContent = 'Vidas: ' + lives;
  }

  const levelElement = document.getElementById('level-display');
  if (levelElement) {
    levelElement.textContent = 'Nivel: ' + level;
  }

  // Actualizar botones según estado
  const startBtn = document.getElementById('start-btn');
  const pauseBtn = document.getElementById('pause-btn');

  if (startBtn) {
    startBtn.textContent = gameState === 'ready' || gameState === 'gameOver' ? '▶️ JUGAR' : '🔄 RESET';
  }

  if (pauseBtn) {
    pauseBtn.textContent = gameState === 'paused' ? '▶️ REANUDAR' : '⏸️ PAUSA';
  }
}

// Control del mouse para mover paddle
function movePaddle(evt) {
  if (gameState === 'playing' || gameState === 'paused') {
    const svg = evt.currentTarget;
    const rect = svg.getBoundingClientRect();
    const x = evt.clientX - rect.left;
    const svgX = x * (800 / rect.width); // Escalar a coordenadas SVG

    paddle.x = svgX - paddle.width / 2;
    if (paddle.x < 20) paddle.x = 20;
    if (paddle.x > 800 - paddle.width - 20) paddle.x = 800 - paddle.width - 20;

    updateDisplay();
  }
}

// Hacer clic en ladrillo (modo fácil)
function hitBrick(brickId) {
  if (gameState !== 'playing') return;

  const brick = bricks.find(function(b) { return b.id === brickId; });
  if (brick && !brick.destroyed) {
    brick.destroyed = true;
    score += brick.level * 5; // Menos puntos por clic directo

    const brickElement = document.getElementById(brickId);
    if (brickElement) {
      brickElement.classList.add('destroyed');
    }

    updateDisplay();
  }
}

// Inicializar cuando se carga
window.addEventListener('load', function() {
  initGame();
});
